mapper.update:
update inquiry_faq
set inquiry_type = #{inquiry_type}, title = #{title}, content = #{content}, write_date = NOW()
where seq = #{seq}

mapper.select:
select seq, inquiry_type, title, content, write_date from inquiry_faq
where title	LIKE concat ('%',#{keyword},'%')
order by seq desc limit #{startnum}, #{postnum}

mapper.update:
update inquiry_lost
set theaterID = #{theaterID}, title = #{title}, content = #{content}, lost_date = #{lost_date}, status = #{status}, write_date = NOW()
where seq = #{seq}

update inquiry
set theaterID = #{theaterID}, inquiry_type = #{inquiry_type}, title = #{title}, content = #{content}, write_date = NOW()
where seq = #{seq}

mapper.select:
select seq, view_cnt, theaterID, title, write_date, inquiry_type, user_id from inquiry
where title	LIKE concat ('%',#{keyword},'%')
order by seq desc limit #{startnum}, #{postnum}
